package cn.nukkit.network.protocol;

import java.util.Base64;

public class BiomeDefinitionListPacket extends DataPacket {

    public static final byte NETWORK_ID = ProtocolInfo.BIOME_DEFINITION_LIST_PACKET;

    private static final String HARDCODED_NBT_BLOB = "";

    public byte[] namedtag = Base64.getDecoder().decode(HARDCODED_NBT_BLOB);

    @Override
    public byte pid() {
        return NETWORK_ID;
    }

    @Override
    public void decode() {
        this.namedtag = this.get();
    }

    @Override
    public void encode() {
        this.reset();
        this.put(this.namedtag);
    }
}
